import React from 'react';
import { Card, Image } from 'react-bootstrap';

const Product = ({ product }) => {
  console.log(product);
  return (
    <Card className="my-3 p-3 rounded">
      <a href={`/product/${product._id}`}>
        <Image src={product.image} variant="top" thumbnail fluid />
        {/* <Card.Img variant='top' src={product.image} /> */}
      </a>
      <span>
      <Card.Body>
        <a href={`/product/${product._id}`}>
          <Card.Title as="div">
            <strong>Bank Name: {product.bankName}</strong>
          </Card.Title>
        </a>
        <Card.Text as="div">
          <div className="my-3">Card ID: ${product.creditLimit.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}</div>
        </Card.Text>

        <Card.Text as="div">
          <div className="my-3">Credit Limit: ${product.creditLimit.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}</div>
        </Card.Text>

      </Card.Body>
      </span>

    </Card>
  );
};

export default Product;
